import { defineConfig } from "vite";
import react from "@vitejs/plugin-react";
import path from "path";
import runtimeErrorOverlay from "@replit/vite-plugin-runtime-error-modal";

export default defineConfig({
  plugins: [
    react(),
    runtimeErrorOverlay(),
    ...(process.env.NODE_ENV !== "production" &&
    process.env.REPL_ID !== undefined
      ? [
          await import("@replit/vite-plugin-cartographer").then((m) =>
            m.cartographer(),
          ),
        ]
      : []),
  ],
  resolve: {
    alias: {
      "@": path.resolve(import.meta.dirname, "client", "src"),
      "@shared": path.resolve(import.meta.dirname, "shared"),
      "@assets": path.resolve(import.meta.dirname, "attached_assets"),
    },
  },
  root: path.resolve(import.meta.dirname, "client"),
  build: {
    outDir: path.resolve(import.meta.dirname, "dist/public"),
    emptyOutDir: true,
  },
  server: {
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { verifyFirebaseToken } from "./services/auth";
import { createRazorpayOrder, verifyRazorpayPayment } from "./services/payment";
import { sendEmail } from "./services/email";
import { insertUserSchema, insertSessionSchema, insertActivityLogSchema, insertUsageTrackingSchema, insertPaymentSchema } from "@shared/schema";
import { z } from "zod";

// WebSocket connection tracking
const sessionConnections = new Map<string, Set<WebSocket>>();

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // Setup WebSocket server
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  wss.on('connection', (ws: WebSocket) => {
    console.log('WebSocket client connected');

    ws.on('message', (data) => {
      try {
        const message = JSON.parse(data.toString());

        if (message.type === 'subscribe' && message.sessionId) {
          // Subscribe to session updates
          if (!sessionConnections.has(message.sessionId)) {
            sessionConnections.set(message.sessionId, new Set());
          }
          sessionConnections.get(message.sessionId)!.add(ws);
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', () => {
      // Remove from all session subscriptions
      for (const [sessionId, connections] of sessionConnections.entries()) {
        connections.delete(ws);
        if (connections.size === 0) {
          sessionConnections.delete(sessionId);
        }
      }
    });
  });

  // Broadcast message to session subscribers
  function broadcastToSession(sessionId: string, message: any) {
    const connections = sessionConnections.get(sessionId);
    if (connections) {
      const messageStr = JSON.stringify(message);
      connections.forEach(ws => {
        if (ws.readyState === WebSocket.OPEN) {
          ws.send(messageStr);
        }
      });
    }
  }

  // Auth routes
  app.post('/api/auth/verify', async (req, res) => {
    try {
      const { token, email, displayName, photoURL, firebaseUid } = req.body;

      // Verify Firebase token
      const decodedToken = await verifyFirebaseToken(token);

      if (decodedToken.uid !== firebaseUid) {
        return res.status(401).json({ message: 'Invalid token' });
      }

      // Get or create user
      let user = await storage.getUserByFirebaseUid(firebaseUid);

      if (!user) {
        const userData = insertUserSchema.parse({
          email,
          displayName,
          photoURL,
          firebaseUid,
        });
        user = await storage.createUser(userData);
      }

      res.json(user);
    } catch (error) {
      console.error('Auth verification error:', error);
      res.status(401).json({ message: 'Authentication failed' });
    }
  });

  // Session routes
  app.post('/api/sessions', async (req, res) => {
    try {
      const sessionData = insertSessionSchema.parse(req.body);

      // Check trial usage limits
      const todayUsage = await storage.getTodayUsage(sessionData.userId);
      const user = await storage.getUser(sessionData.userId);

      if (user?.subscriptionTier === 'trial') {
        if (todayUsage && todayUsage.minutesUsed >= 15) {
          return res.status(429).json({ message: 'Daily trial limit exceeded' });
        }
      }

      const session = await storage.createSession(sessionData);

      // Start browser automation (call Python backend)
      try {
        const response = await fetch('http://localhost:8001/start-session', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            sessionId: session.id,
            taskDescription: session.taskDescription,
            model: session.modelUsed,
          }),
        });

        if (response.ok) {
          await storage.updateSession(session.id, { status: 'running' });

          // Log activity
          await storage.createActivityLog({
            sessionId: session.id,
            message: 'Browser automation session started',
            status: 'success',
          });

          broadcastToSession(session.id, {
            type: 'status',
            data: { status: 'running' },
            timestamp: new Date().toISOString(),
          });
        }
      } catch (error) {
        console.error('Failed to start Python backend session:', error);
        await storage.updateSession(session.id, { status: 'failed' });
      }

      res.json(session);
    } catch (error) {
      console.error('Session creation error:', error);
      res.status(400).json({ message: 'Failed to create session' });
    }
  });

  app.patch('/api/sessions/:id', async (req, res) => {
    try {
      const { id } = req.params;
      const updates = req.body;

      const session = await storage.updateSession(id, updates);
      if (!session) {
        return res.status(404).json({ message: 'Session not found' });
      }

      // Notify Python backend of status change
      if (updates.status) {
        try {
          await fetch('http://localhost:8001/update-session', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
              sessionId: id,
              status: updates.status,
            }),
          });
        } catch (error) {
          console.error('Failed to update Python backend session:', error);
        }
      }

      broadcastToSession(id, {
        type: 'status',
        data: { status: updates.status },
        timestamp: new Date().toISOString(),
      });

      res.json(session);
    } catch (error) {
      console.error('Session update error:', error);
      res.status(400).json({ message: 'Failed to update session' });
    }
  });

  app.get('/api/session/:id/viewport', (req, res) => {
    const { id } = req.params;
    // Proxy to Python backend viewport stream
    res.redirect(`http://localhost:8001/viewport/${id}`);
  });

  // Usage tracking routes
  app.get('/api/usage/:userId', async (req, res) => {
    try {
      const { userId } = req.params;
      const todayUsage = await storage.getTodayUsage(userId);
      const allUsage = await storage.getUsageByUser(userId);

      // Calculate trial days used
      const thirtyDaysAgo = new Date();
      thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

      const recentUsage = allUsage.filter(usage => 
        new Date(usage.date) >= thirtyDaysAgo
      );

      const trialDaysUsed = recentUsage.length;
      const minutesUsed = todayUsage?.minutesUsed || 0;

      res.json({
        minutesUsed,
        trialDaysUsed,
        firstTrialDate: recentUsage[0]?.date,
      });
    } catch (error) {
      console.error('Usage tracking error:', error);
      res.status(500).json({ message: 'Failed to get usage data' });
    }
  });

  // Payment routes
  app.post('/api/payments/create-order', async (req, res) => {
    try {
      const { userId, planType, amount } = req.body;

      const order = await createRazorpayOrder(amount, 'INR');

      // Store payment record
      const payment = await storage.createPayment({
        userId,
        razorpayOrderId: order.id,
        amount: (amount / 100).toString(), // Convert from paise to rupees
        currency: 'INR',
        status: 'pending',
        planType,
      });

      res.json({
        orderId: order.id,
        amount: order.amount,
        currency: order.currency,
        paymentId: payment.id,
      });
    } catch (error) {
      console.error('Payment order creation error:', error);
      res.status(500).json({ message: 'Failed to create payment order' });
    }
  });

  app.post('/api/payments/verify', async (req, res) => {
    try {
      const { razorpay_order_id, razorpay_payment_id, razorpay_signature, userId } = req.body;

      const isValid = verifyRazorpayPayment(razorpay_order_id, razorpay_payment_id, razorpay_signature);

      if (!isValid) {
        return res.status(400).json({ message: 'Invalid payment signature' });
      }

      // Update payment status
      const payments = await storage.getPaymentsByUser(userId);
      const payment = payments.find(p => p.razorpayOrderId === razorpay_order_id);

      if (payment) {
        await storage.updatePayment(payment.id, {
          razorpayPaymentId: razorpay_payment_id,
          status: 'success',
          subscriptionEndDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000).toISOString(), // 30 days
        });

        // Update user subscription
        await storage.updateUser(userId, {
          subscriptionTier: 'premium',
        });

        // Send confirmation email
        const user = await storage.getUser(userId);
        if (user?.email) {
          try {
            await sendEmail({
              to: user.email,
              from: 'noreply@agentbrowse.com',
              subject: 'Welcome to AgentBrowse Premium!',
              html: `
                <h1>Thank you for upgrading to Premium!</h1>
                <p>Your subscription is now active and you have unlimited automation time.</p>
                <p>Start automating at: https://agentbrowse.com/dashboard</p>
              `,
            });
          } catch (emailError) {
            console.error('Failed to send confirmation email:', emailError);
          }
        }
      }

      res.json({ success: true });
    } catch (error) {
      console.error('Payment verification error:', error);
      res.status(500).json({ message: 'Payment verification failed' });
    }
  });

  // Admin routes
  app.get('/api/admin/stats', async (req, res) => {
    try {
      const stats = await storage.getStats();
      res.json(stats);
    } catch (error) {
      console.error('Admin stats error:', error);
      res.status(500).json({ message: 'Failed to get stats' });
    }
  });

  app.get('/api/admin/recent-activity', async (req, res) => {
    try {
      const sessions = await storage.getAllSessions();
      const users = await storage.getAllUsers();

      const recentSessions = sessions
        .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime())
        .slice(0, 10);

      const activity = recentSessions.map(session => {
        const user = users.find(u => u.id === session.userId);
        return {
          user: {
            email: user?.email,
            displayName: user?.displayName,
            subscriptionTier: user?.subscriptionTier,
          },
          action: session.taskDescription.substring(0, 50) + '...',
          duration: session.durationMinutes ? `${session.durationMinutes}:00` : '0:00',
          status: session.status,
          time: new Date(session.createdAt).toLocaleString(),
        };
      });

      res.json(activity);
    } catch (error) {
      console.error('Admin activity error:', error);
      res.status(500).json({ message: 'Failed to get recent activity' });
    }
  });

  // Python backend webhook routes for real-time updates
  app.post('/api/webhook/activity', async (req, res) => {
    try {
      const { sessionId, message, status } = req.body;

      // Store activity log
      await storage.createActivityLog({
        sessionId,
        message,
        status: status || 'info',
      });

      // Broadcast to connected clients
      broadcastToSession(sessionId, {
        type: 'activity',
        data: { sessionId, message, status: status || 'info' },
        timestamp: new Date().toISOString(),
      });

      res.json({ success: true });
    } catch (error) {
      console.error('Activity webhook error:', error);
      res.status(500).json({ message: 'Failed to process activity' });
    }
  });

  app.post('/api/webhook/viewport-update', async (req, res) => {
    try {
      const { sessionId, currentUrl } = req.body;

      // Update session URL
      await storage.updateSession(sessionId, { currentUrl });

      // Broadcast viewport update
      broadcastToSession(sessionId, {
        type: 'status',
        data: { currentUrl },
        timestamp: new Date().toISOString(),
      });

      res.json({ success: true });
    } catch (error) {
      console.error('Viewport webhook error:', error);
      res.status(500).json({ message: 'Failed to update viewport' });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { type User, type InsertUser, type Session, type InsertSession, type ActivityLog, type InsertActivityLog, type UsageTracking, type InsertUsageTracking, type Payment, type InsertPayment } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // User methods
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  getUserByFirebaseUid(firebaseUid: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, updates: Partial<User>): Promise<User | undefined>;

  // Session methods
  getSession(id: string): Promise<Session | undefined>;
  getSessionsByUser(userId: string): Promise<Session[]>;
  createSession(session: InsertSession): Promise<Session>;
  updateSession(id: string, updates: Partial<Session>): Promise<Session | undefined>;

  // Activity log methods
  getActivityLogsBySession(sessionId: string): Promise<ActivityLog[]>;
  createActivityLog(log: InsertActivityLog): Promise<ActivityLog>;

  // Usage tracking methods
  getUsageByUser(userId: string): Promise<UsageTracking[]>;
  getTodayUsage(userId: string): Promise<UsageTracking | undefined>;
  createOrUpdateUsage(usage: InsertUsageTracking): Promise<UsageTracking>;

  // Payment methods
  getPaymentsByUser(userId: string): Promise<Payment[]>;
  createPayment(payment: InsertPayment): Promise<Payment>;
  updatePayment(id: string, updates: Partial<Payment>): Promise<Payment | undefined>;

  // Admin methods
  getAllUsers(): Promise<User[]>;
  getAllSessions(): Promise<Session[]>;
  getStats(): Promise<any>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User> = new Map();
  private sessions: Map<string, Session> = new Map();
  private activityLogs: Map<string, ActivityLog> = new Map();
  private usageTracking: Map<string, UsageTracking> = new Map();
  private payments: Map<string, Payment> = new Map();

  // User methods
  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.email === email);
  }

  async getUserByFirebaseUid(firebaseUid: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(user => user.firebaseUid === firebaseUid);
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const now = new Date().toISOString();
    const user: User = {
      ...insertUser,
      id,
      isAdmin: false,
      subscriptionTier: 'trial',
      createdAt: now,
      updatedAt: now,
    };
    this.users.set(id, user);
    return user;
  }

  async updateUser(id: string, updates: Partial<User>): Promise<User | undefined> {
    const user = this.users.get(id);
    if (!user) return undefined;

    const updatedUser = {
      ...user,
      ...updates,
      updatedAt: new Date().toISOString(),
    };
    this.users.set(id, updatedUser);
    return updatedUser;
  }

  // Session methods
  async getSession(id: string): Promise<Session | undefined> {
    return this.sessions.get(id);
  }

  async getSessionsByUser(userId: string): Promise<Session[]> {
    return Array.from(this.sessions.values()).filter(session => session.userId === userId);
  }

  async createSession(insertSession: InsertSession): Promise<Session> {
    const id = randomUUID();
    const now = new Date().toISOString();
    const session: Session = {
      ...insertSession,
      id,
      status: 'pending',
      durationMinutes: 0,
      createdAt: now,
    };
    this.sessions.set(id, session);
    return session;
  }

  async updateSession(id: string, updates: Partial<Session>): Promise<Session | undefined> {
    const session = this.sessions.get(id);
    if (!session) return undefined;

    const updatedSession = { ...session, ...updates };
    this.sessions.set(id, updatedSession);
    return updatedSession;
  }

  // Activity log methods
  async getActivityLogsBySession(sessionId: string): Promise<ActivityLog[]> {
    return Array.from(this.activityLogs.values())
      .filter(log => log.sessionId === sessionId)
      .sort((a, b) => new Date(b.timestamp).getTime() - new Date(a.timestamp).getTime());
  }

  async createActivityLog(insertLog: InsertActivityLog): Promise<ActivityLog> {
    const id = randomUUID();
    const log: ActivityLog = {
      ...insertLog,
      id,
      timestamp: new Date().toISOString(),
    };
    this.activityLogs.set(id, log);
    return log;
  }

  // Usage tracking methods
  async getUsageByUser(userId: string): Promise<UsageTracking[]> {
    return Array.from(this.usageTracking.values()).filter(usage => usage.userId === userId);
  }

  async getTodayUsage(userId: string): Promise<UsageTracking | undefined> {
    const today = new Date().toDateString();
    return Array.from(this.usageTracking.values()).find(
      usage => usage.userId === userId && new Date(usage.date).toDateString() === today
    );
  }

  async createOrUpdateUsage(insertUsage: InsertUsageTracking): Promise<UsageTracking> {
    const existing = await this.getTodayUsage(insertUsage.userId);

    if (existing) {
      const updated = {
        ...existing,
        minutesUsed: (existing.minutesUsed || 0) + (insertUsage.minutesUsed || 0),
        sessionsCount: (existing.sessionsCount || 0) + (insertUsage.sessionsCount || 0),
        trialDaysUsed: insertUsage.trialDaysUsed || existing.trialDaysUsed,
        firstTrialDate: existing.firstTrialDate || insertUsage.firstTrialDate,
      };
      this.usageTracking.set(existing.id, updated);
      return updated;
    }

    const id = randomUUID();
    const usage: UsageTracking = {
      ...insertUsage,
      id,
      date: new Date().toISOString(),
    };
    this.usageTracking.set(id, usage);
    return usage;
  }

  // Payment methods
  async getPaymentsByUser(userId: string): Promise<Payment[]> {
    return Array.from(this.payments.values()).filter(payment => payment.userId === userId);
  }

  async createPayment(insertPayment: InsertPayment): Promise<Payment> {
    const id = randomUUID();
    const payment: Payment = {
      ...insertPayment,
      id,
      createdAt: new Date().toISOString(),
    };
    this.payments.set(id, payment);
    return payment;
  }

  async updatePayment(id: string, updates: Partial<Payment>): Promise<Payment | undefined> {
    const payment = this.payments.get(id);
    if (!payment) return undefined;

    const updatedPayment = { ...payment, ...updates };
    this.payments.set(id, updatedPayment);
    return updatedPayment;
  }

  // Admin methods
  async getAllUsers(): Promise<User[]> {
    return Array.from(this.users.values());
  }

  async getAllSessions(): Promise<Session[]> {
    return Array.from(this.sessions.values());
  }

  async getStats(): Promise<any> {
    const users = Array.from(this.users.values());
    const sessions = Array.from(this.sessions.values());
    const today = new Date().toDateString();

    const totalUsers = users.length;
    const activeSubscribers = users.filter(u => u.subscriptionTier === 'premium').length;
    const sessionsToday = sessions.filter(s => new Date(s.createdAt).toDateString() === today).length;
    const completedSessions = sessions.filter(s => s.status === 'completed').length;
    const successRate = sessions.length > 0 ? ((completedSessions / sessions.length) * 100).toFixed(1) : '0';

    return {
      totalUsers,
      activeSubscribers,
      sessionsToday,
      successRate,
    };
  }
}

export const storage = new MemStorage();

