import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { Play, Pause, Square } from 'lucide-react';
import { useAuth } from '@/hooks/use-auth';
import { useToast } from '@/hooks/use-toast';
import { apiRequest } from '@/lib/queryClient';

interface TaskControlProps {
  onSessionStart: (sessionId: string) => void;
  onSessionStop: () => void;
  currentSession?: string;
  sessionStatus?: 'pending' | 'running' | 'completed' | 'failed' | 'paused';
  remainingTime?: string;
  className?: string;
}

export function TaskControl({ 
  onSessionStart, 
  onSessionStop, 
  currentSession, 
  sessionStatus,
  remainingTime = '00:00',
  className 
}: TaskControlProps) {
  const [taskDescription, setTaskDescription] = useState('');
  const [selectedModel, setSelectedModel] = useState('gpt-4');
  const [isStarting, setIsStarting] = useState(false);
  const { user } = useAuth();
  const { toast } = useToast();

  const handleStartSession = async () => {
    if (!taskDescription.trim()) {
      toast({
        title: "Task Required",
        description: "Please describe what you want the browser agent to do",
        variant: "destructive",
      });
      return;
    }

    if (!user) {
      toast({
        title: "Authentication Required",
        description: "Please sign in to start a session",
        variant: "destructive",
      });
      return;
    }

    setIsStarting(true);
    try {
      const response = await apiRequest('POST', '/api/sessions', {
        userId: user.id,
        taskDescription: taskDescription.trim(),
        modelUsed: selectedModel,
      });

      const session = await response.json();
      onSessionStart(session.id);

      toast({
        title: "Session Started",
        description: "Browser automation has begun",
      });
    } catch (error) {
      console.error('Error starting session:', error);
      toast({
        title: "Error",
        description: "Failed to start automation session",
        variant: "destructive",
      });
    } finally {
      setIsStarting(false);
    }
  };

  const handlePauseSession = async () => {
    if (!currentSession) return;

    try {
      await apiRequest('PATCH', `/api/sessions/${currentSession}`, {
        status: 'paused',
      });

      toast({
        title: "Session Paused",
        description: "Browser automation has been paused",
      });
    } catch (error) {
      console.error('Error pausing session:', error);
      toast({
        title: "Error",
        description: "Failed to pause session",
        variant: "destructive",
      });
    }
  };

  const handleStopSession = async () => {
    if (!currentSession) return;

    try {
      await apiRequest('PATCH', `/api/sessions/${currentSession}`, {
        status: 'completed',
        endTime: new Date().toISOString(),
      });

      onSessionStop();

      toast({
        title: "Session Stopped",
        description: "Browser automation has been stopped",
      });
    } catch (error) {
      console.error('Error stopping session:', error);
      toast({
        title: "Error",
        description: "Failed to stop session",
        variant: "destructive",
      });
    }
  };

  const isRunning = sessionStatus === 'running';
  const isPaused = sessionStatus === 'paused';
  const hasActiveSession = currentSession && (isRunning || isPaused);

  return (
    <div className={className}>
      {/* Task Input Section */}
      <div className="p-6 border-b border-gray-200">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Browser Automation</h2>
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Task Description
            </label>
            <Textarea
              value={taskDescription}
              onChange={(e) => setTaskDescription(e.target.value)}
              placeholder="Describe what you want the browser agent to do..."
              rows={3}
              disabled={hasActiveSession}
              data-testid="task-input"
            />
          </div>
          <div className="grid grid-cols-2 gap-3">
            <Select
              value={selectedModel}
              onValueChange={setSelectedModel}
              disabled={hasActiveSession}
            >
              <SelectTrigger data-testid="model-select">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="gpt-4">GPT-4 Turbo</SelectItem>
                <SelectItem value="claude-3.5">Claude-3.5</SelectItem>
                <SelectItem value="gemini-pro">Gemini Pro</SelectItem>
              </SelectContent>
            </Select>
            <div className="text-sm text-gray-600 bg-gray-50 p-2 rounded-lg flex items-center justify-center">
              <Badge variant="outline" data-testid="remaining-time">
                {remainingTime} remaining today
              </Badge>
            </div>
          </div>
        </div>
      </div>

      {/* Control Buttons */}
      <div className="p-6 border-b border-gray-200">
        <div className="flex space-x-3">
          {!hasActiveSession ? (
            <Button
              onClick={handleStartSession}
              disabled={isStarting || !taskDescription.trim()}
              variant="success"
              className="flex-1"
              data-testid="button-start"
            >
              <Play className="w-4 h-4 mr-2" />
              {isStarting ? 'Starting...' : 'Start Agent'}
            </Button>
          ) : (
            <>
              {isRunning && (
                <Button
                  onClick={handlePauseSession}
                  variant="warning"
                  data-testid="button-pause"
                >
                  <Pause className="w-4 h-4" />
                </Button>
              )}
              {isPaused && (
                <Button
                  onClick={handleStartSession}
                  variant="success"
                  data-testid="button-resume"
                >
                  <Play className="w-4 h-4" />
                </Button>
              )}
              <Button
                onClick={handleStopSession}
                variant="danger"
                className="flex-1"
                data-testid="button-stop"
              >
                <Square className="w-4 h-4 mr-2" />
                Stop Agent
              </Button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/hooks/use-auth.tsx.content:
import { useState, useEffect, createContext, useContext } from 'react';
import { onAuthStateChange, signInWithGoogle, signOutUser } from '@/lib/firebase';
import { apiRequest } from '@/lib/queryClient';
import type { User } from '@shared/schema';

interface AuthContextType {
  user: User | null;
  firebaseUser: any;
  loading: boolean;
  signIn: () => Promise<void>;
  signOut: () => Promise<void>;
  checkTrialUsage: () => Promise<any>;
}

const AuthContext = createContext<AuthContextType | null>(null);

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [firebaseUser, setFirebaseUser] = useState<any>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const unsubscribe = onAuthStateChange(async (firebaseUser) => {
      setFirebaseUser(firebaseUser);

      if (firebaseUser) {
        try {
          // Get or create user in our database
          const token = await firebaseUser.getIdToken();
          const response = await apiRequest('POST', '/api/auth/verify', {
            token,
            email: firebaseUser.email,
            displayName: firebaseUser.displayName,
            photoURL: firebaseUser.photoURL,
            firebaseUid: firebaseUser.uid,
          });

          const userData = await response.json();
          setUser(userData);
        } catch (error) {
          console.error('Error verifying user:', error);
          setUser(null);
        }
      } else {
        setUser(null);
      }

      setLoading(false);
    });

    return unsubscribe;
  }, []);

  const signIn = async () => {
    try {
      await signInWithGoogle();
    } catch (error) {
      console.error('Sign in error:', error);
      throw error;
    }
  };

  const signOut = async () => {
    try {
      await signOutUser();
      setUser(null);
      setFirebaseUser(null);
    } catch (error) {
      console.error('Sign out error:', error);
      throw error;
    }
  };

  const checkTrialUsage = async () => {
    if (!user) return null;

    try {
      const response = await apiRequest('GET', `/api/usage/${user.id}`);
      return await response.json();
    } catch (error) {
      console.error('Error checking trial usage:', error);
      return null;
    }
  };

  return (
    <AuthContext.Provider value={{
      user,
      firebaseUser,
      loading,
      signIn,
      signOut,
      checkTrialUsage,
    }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
}

